import wordList_normal from '../wordList_normal.txt';
import wordList_hard from '../wordList_hard.txt';
import EasyLevel from './EasyLevel';
//import wordList from '../tempWordList.txt';

export const defaultGrid = [
  ['', '', '', '', '', ''],
  ['', '', '', '', '', ''],
  ['', '', '', '', '', ''],
