import React, { useContext, useEffect } from 'react';
import './EasyLevel.css';
import MainGrid from './MainGrid';
import { useState } from 'react';
import { AppContext } from './EasyLevel';
import '../App.css';

// TODO change it to alphaPos and enteredVal

function Alphabet({ letterPos, attemptVal }) {
  const { grid, correctWord, currAttempt } = useContext(AppContext);
  const alphabet = grid[attemptVal][letterPos];

  // TODO: change the logic here in if else statements
  const correct = correctWord[letterPos] === alphabet;
  const almost = !correct && alphabet !== '' && correctWord.includes(alphabet);

  const letterState =
    currAttempt.attempt > attemptVal &&
    (correct ? 'correct' : almost ? 'almost' : 'error');

  useEffect(() => {
    if (alphabet !== '' && !correct && !almost) return;
  }, [currAttempt.attempt]);

  return (
    <div className="alphabet" id={letterState}>
      {alphabet}
    </div>
  );
}

export default Alphabet;
