  const keys_row1 = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'];
  const keys_row2 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'];
  const keys_row3 = ['\u23CE', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', '\u232b'];

  // const keys1 = [
