  //   ['ENTER', 'z', 'x', 'c', 'v', 'b', 'n', 'm', '\u232b'],
  // ];
  console.log(props);
  const { grid, setGrid } = useContext(AppContext);
  const selectKey = (keyVal) => {
    const newGrid = [...grid];
    newGrid[0][0] = keyVal;
    setGrid(newGrid);
  };

  // const keyPressed = () => {
  //   const newGrid = [...grid];
  //   newGrid[0][0] = props;
  //   setGrid(newGrid);
  // };

  //let iterator = keys_row1.values();
  // return (
  //   <div className="keyboard">
  //     {keys1.map((item, index) => (
  //       <div className="row1" key={index}>
  //         {item.map((key, keyIndex) => (
  //           <button key={keyIndex} onClick={() => props.onClick()}>
  //             {key}{' '}
  //           </button>
  //         ))}
  //       </div>
  //     ))}
  //   </div>
  // );

  return (
    <div>
      <div className="row1">
        {keys_row1.map((key1) => {
          return (
            <div className="key" keyVal={key1} onClick={selectKey}>
              {key1}
            </div>
          );
        })}
      </div>
      <div className="row1">
        {keys_row2.map((key1) => {
          return <div className="key"> {key1}</div>;
        })}
      </div>
      <div className="row1">
        {keys_row3.map((key1) => {
          return <div className="key"> {key1}</div>;
        })}
      </div>
    </div>
  );
}

export default Keyboard;
