import React from 'react';
import MainGrid from './MainGrid';
import Keyboard from './Keyboard';
import { createContext } from 'react';
import { useState } from 'react';
import { defaultGrid } from './EmptyGrid';

export const AppContext = createContext();
function EasyLevel() {
  const EasyLevel = () => <h1>Easy Level</h1>;
  const [grid, setGrid] = useState(defaultGrid);
  const [currAttempt, setCurrAttempt] = useState({ attempt: 0, alphaPos: 0 });

  const onSelectKey = () => {
    if (currAttempt.letterPos > 4) return;
    const newGrid = [...grid];
    newGrid[currAttempt.attempt][currAttempt.letterPos] = keyVal;
    setGrid(newGrid);
    setCurrAttempt({ ...currAttempt, letterPos: currAttempt.letterPos + 1 });
  };

  const onDelete = () => {
    if (currAttempt.letterPos === 0) return;
    const newGrid = [...grid];
    newGrid[currAttempt.attempt][currAttempt.letterPos - 1] = '';
    setGrid(newGrid);
    setCurrAttempt({ ...currAttempt, letterPos: currAttempt.letterPos - 1 });
  };

  const onEnter = () => {
    if (currAttempt.letterPos !== 5) return;
    setCurrAttempt({ attempt: currAttempt.attempt + 1, letterPos: 0 });
  };

  return (
    <div>
      <AppContext.Provider
        value={{ grid, setGrid, currAttempt, setCurrAttempt }}
      >
        <MainGrid />
        <Keyboard />
      </AppContext.Provider>
    </div>
  );
}

export default EasyLevel;
