import React, { useContext } from 'react';
import '../App.css';
import { AppContext } from './EasyLevel';

function Keyboard(val) {
  // const keys_row1 = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'];
  // const keys_row2 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'];
  // const keys_row3 = ['Z', 'X', 'C', 'V', 'B', 'N', 'M'];

  const keys1 = [
    ['q', 'w', 'e', 'r', 't', 'y', 'u', 'i', 'o', 'p'],
    ['a', 's', 'd', 'f', 'g', 'h', 'j', 'k', 'l'],
    ['ENTER', 'z', 'x', 'c', 'v', 'b', 'n', 'm', '\u232b'],
  ];

  const { grid, setGrid } = useContext(AppContext);

  const keyPressed = () => {
    const newGrid = [...grid];
    newGrid[0][0] = val;
    setGrid(newGrid);
  };

  //let iterator = keys_row1.values();
  return (
    <div className="keyboard">
      {keys1.map((item, index) => (
        <div className="row1" key={index}>
          {item.map((key, keyIndex) => (
            <button key={keyIndex} onClick={keyPressed}>
              {key}{' '}
            </button>
          ))}
        </div>
      ))}
    </div>
  );
}
