import React, { useContext } from 'react';
import { AppContext } from './EasyLevel';

import '../App.css';

function Key({ keyVal }) {
  //const { gameOver, onSelectKey, onDelete, onEnter } = useContext(AppContext);

  const { grid, setGrid, currAttempt, setCurrAttempt } = useContext(AppContext);

  const selectKey = () => {
    const newGrid = [...grid];
    newGrid[currAttempt.attempt][currAttempt.alphaPos] = keyVal;
    setGrid(newGrid);
    setCurrAttempt({ ...currAttempt, alphaPos: currAttempt.alphaPos + 1 });
  };

  return (
    <div className="key" onClick={selectKey}>
      {keyVal}
    </div>
  );
}
export default Key;
